library(MASS)
library(parallel)
d2 &lt;- galaxies


## normal
tboot1 &lt;- numeric(1000)

system.time({
  for (i in 1:1000){
  tboot1[i] &lt;- median(sample(d2, replace = TRUE))
  }
})

