## foreach doParellel
c1 &lt;- makeCluster(3)
clusterExport(c1, "d2")
registerDoParallel(c1)

system.time({
tboot2 &lt;- foreach(i = 1:1000, .combine = c) %dopar% {
  median(sample(d2, replace = TRUE))
}
})

   user  system elapsed 
   0.25    0.03    0.30 

## large sample

system.time({
tboot3 &lt;- foreach(i = 1:10000, .combine = c) %dopar% {
  median(sample(d2, replace = TRUE))
}
})

   user  system elapsed 
   2.03    0.58    2.65 

stopCluster(c1)

2.4 Question 3

coverage_count &lt;- 0
mb3 &lt;- numeric(1000)
for (i in 1:1000) {

    s &lt;- rexp(50, 1)
    for (i in 1:1000){
    b3 &lt;- sample(s, replace = TRUE)
    mb3[i] &lt;- mean(b3)
    }
    ci &lt;- quantile(mb3, c(0.025, 0.975))

    if (ci[1] &lt;= 1 && 1 &lt;= ci[2]) {
      coverage_count &lt;- coverage_count + 1
    }
  }

  coverage_probability &lt;- coverage_count / 1000

coverage_probability

